css:
/* REGIAO ACIMA DO CABECALHO, ONDE FICA AS INFORMACOES DE REDES SOCIAIS E CONTATOS */

.top-bar {
    background: #2d2b2a;
}

.top-bar__nav {
    color:#D2AEA2;
}

.top-bar__nav a:hover {
    color: #FFFFFF;
 }

/*CABECALHO NO GERAL*/

.header {
    background: #D2AEA2;
    border-bottom: 1px solid #e8c8bb;
}

/*ONDE FICAM AS CATEGORIAS MENU*/
.header__nav a {
    color: #2D2B2A;
}

.header__nav a:hover {
   background: #E8C8BB;
}

/*RODAPE (FOOTER)*/

.footer {
    background: #E8C8BB;
    color: #2D2B2A;
}

.footer a, .footer__store, .footer__tittle, .footer__title{
    color:#2D2B2A;
}

/*RODAPE (FOOTER) FINAL*/
.ecom-credits {
    background: #D2AEA2;
    color: #2D2B2A;
}
